content:

          一口气订购126架五代半战机，军方：全面超越歼20，轻松横扫印度洋自动播放                       


      进入广告


.txp_zt_video {
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 2;
}

.txp_zt_video_ad_mask {
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  pointer-events: none;
}

.txp_zt_video_content {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #1c1c1c;
  border-radius: 4px;
  overflow: hidden;
  cursor: pointer;
  pointer-events: auto;
}

.txp_zt_video_loading {
  z-index: 2;
  background-color: #1c1c1c;
  background-position: center;
  background-size: 100% 100%;
  background-repeat: no-repeat;
  pointer-events: unset;
  cursor: auto;
}

.txp_zt_video_err_tips {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 2;
  background-color: #1c1c1c;
  cursor: auto;
}

.txp_zt_video_err_tips_text {
  margin-bottom: 20px;
  font-size: 16px;
}

.txp_zt_video_err_tips_btn {
  width: 90px;
  height: 30px;
  border-radius: 15px;
  background-color: rgba(255, 255, 255, 0.1);
  line-height: 30px;
  text-align: center;
  font-size: 12px;
  cursor: pointer;
}

.txp_zt_video_video {
  width: 100%;
  height: 100%;
}

.txp_zt_video_close {
  display: flex;
  align-items: center;
  position: absolute;
  top: 5px;
  right: 5px;
  width: 58px;
  height: 26px;
  padding-left: 10px;
  z-index: 3;
  border-radius: 2px;
  background-color: rgba(0, 0, 0, 0.5);
  line-height: 26px;
  font-size: 12px;
  cursor: pointer;
}

.txp_zt_video_close:hover {
  background-color: rgba(0, 0, 0, 0.8);
}

.txp_zt_video_close img {
  display: block;
  width: 14px;
  height: 14px;
  margin-left: 2px;
}

.txp_zt_video_detail {
  display: flex;
  align-items: center;
  position: absolute;
  right: 10px;
  bottom: 12px;
  width: 100px;
  height: 30px;
  padding: 0 15px;
  border-radius: 15px;
  background-color: #fe5c38;
  font-size: 12px;
  cursor: pointer;
}
.txp_zt_video_detail.active_ad{
  bottom: 18px;
}

.txp_zt_video_detail:hover {
  background-color: #FF183E;
}

.txp_zt_video_detail img {
  width: 16px;
  height: 16px;
  margin-right: 6px;
}

.txp_zt_video_mute {
  position: absolute;
  bottom: 12px;
  left: 10px;
  width: 30px;
  height: 30px;
  padding: 5px;
  border-radius: 50%;
  cursor: pointer;
  fill: #fff;
}
/* 竞价广告的声音按钮上移 */
.txp_zt_video_mute.active_ad {
  bottom: 82px;
}

.txp_zt_video_mute:hover {
  background-color: rgba(0, 0, 0, 0.8);
}

.txp_zt_video_mute .txp_svg_symbol {
  display: none;
}

.txp_zt_video_mute.volume .txp_svg_volume {
  display: block;
}

.txp_zt_video_mute.mute .txp_svg_volume_mute {
  display: block;
}

.txp_zt_video_progress {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 4px;
  background-color: rgba(255, 255, 255, 0.45);
  cursor: auto;
}

/* 竞价广告的进度条上移 */
.txp_zt_video_progress.active_ad {
  bottom: 66px;
}

.txp_zt_video_progress .progress {
  width: 0;
  height: 4px;
  background-color: #fe5c38;
  border-top-right-radius: 2px;
  border-bottom-right-radius: 2px;
  transition: width 0.15s;
}

.txp_play_icon {
  width: 10%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  pointer-events: auto;
}

.bottom_desc_text_container{
  display: none;
  position: absolute;
  left: 0;
  bottom: 0;
  width: 100%;
  height: 66px;
  background-color: rgba(31, 31, 31, 0.9);
}
.bottom_desc_text_container.active_ad{display: block;}
.bottom_desc_text {
  height: 66px;
  line-height: 66px;
  text-indent: 15px;
  font-size: 18px;
  color: #fff;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
}
.bottom_desc_text.level-full{width: 645px;}
.bottom_desc_text.level-high-hd{width: 447px;}
.bottom_desc_text.level-hd{width: 306px;}
.bottom_desc_text.level-normal{width: 180px;}
.bottom_desc_text.active_ad{
  display: block;
}

/* 二维码广告 - 顶部半透明文案 */
.translucent-top-text-container{
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 80px;
  background-image: linear-gradient(180deg, rgba(0,0,0,0.50) 0%, rgba(0,0,0,0.00) 93%);
  border-radius: 4px 4px 0 0;
  transition: all .3s ease;
}
.translucent-top-text{
  text-indent: 15px;
  font-family: PingFangSC-Regular;
  font-size: 18px;
  color: #FFFFFF;
  letter-spacing: 0;
  line-height: 48px;
  text-shadow: 0 1px 2px rgba(0,0,0,0.25);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.translucent-top-text.level-full{ width: 540px; }
.translucent-top-text.level-high-hd{ width: 468px; }
.translucent-top-text.level-hd{ width: 342px; }
.translucent-top-text.level-normal{ width: 252px; }

/* 二维码广告 - 右下悬浮二维码 */
.float-qrcode {
  position: absolute;
  bottom: 10px;
  right: 10px;
  width: 110px;
  height: 134px;
  background-color: rgba(0, 0, 0, 0.8);
  border-radius: 4px;
  transition: all .3s ease;
}
.float-qrcode .float-code {
  position: absolute;
  top: 10px;
  left: 10px;
  width: 90px;
  height: 90px;
  background-color: #fff;
}
.float-qrcode .float-code img {
  margin: 5%;
  width: 90%;
  height: 90%;
}
.float-qrcode .scan-tips {
  position: absolute;
  left: 10px;
  bottom: 10px;
  font-family: 'PingFangSC-Medium';
  font-size: 14px;
  color: #FFFFFF;
  letter-spacing: 1px;
  line-height: 14px;
}

/* 二维码广告 - 右侧二维码 */
.right-qrcode {
  position: absolute;
  top: 0;
  right: -270px;
  width: 270px;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background-color: rgba(0,0,0,.5);
  pointer-events: auto;
  transition: all .3s ease;
}
.right-qrcode.level-high-hd .item-avatar-box{ margin-top: 20px; }
.right-qrcode.level-high-hd .item-text{ display: none; }
.right-qrcode.level-high-hd .right-code{ margin-top: 10px; }
.right-qrcode.level-hd .item-avatar-box{ margin-top: 15px; }
.right-qrcode.level-hd .item-title{ margin-top: 5px; }
.right-qrcode.level-hd .item-text{ display: none; }
.right-qrcode.level-hd .right-code{ 
  margin-top: 5px;
  width: 136px;
  height: 136px;
}
.right-qrcode.level-normal .item-avatar-box{ display: none; }
.right-qrcode.level-normal .item-title{ display: none; }
.right-qrcode.level-normal .item-text{ display: none; }
.right-qrcode.level-normal .right-code{
  margin-top: 10px;
  width: 136px;
  height: 136px;
}
.right-qrcode .item-avatar-box {
  margin-top: 35px;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  overflow: hidden;
}
.right-qrcode .item-avatar-box .item-avatar {
  width: 100%;
  height: 100%;
}
.right-qrcode .item-title {
  margin-top: 10px;
  width: 190px;
  font-size: 14px;
  color: #fff;
  text-align: center;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.right-qrcode .item-text {
  margin-top: 15px;
  width: 200px;
  text-align: center;
  font-size: 14px;
  line-height: 22px;
  color: #fff;
  overflow: hidden;
  text-overflow:ellipsis;
  text-overflow: -o-ellipsis-lastline;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
}
.right-qrcode .right-code {
  margin-top: 20px;
  width: 200px;
  height: 200px;
  background-color: #fff;
}
.right-qrcode .right-code img {
  margin: 5%;
  width: 90%;
  height: 90%;
}
.right-qrcode .scan-tips {
  width: 200px;
  margin-top: 10px;
  margin-bottom: 10px;
  font-family: PingFangSC-Regular;
  font-size: 14px;
  color: #FFFFFF;
  letter-spacing: 1px;
  text-align: center;
  line-height: 14px;
  text-shadow: 0 1px 2px rgba(0,0,0,0.25);
}

/* 二维码广告 - hover动画 */
.txp_zt_video_content:hover .translucent-top-text-container{
  top: -80px;
  transition: all .3s ease;
}
.txp_zt_video_content:hover .float-qrcode{
  right: -120px;
  transition: all .3s ease;
}
.txp_zt_video_content:hover .right-qrcode{
  right: 0;
  transition: all .3s ease;
}

















