    广告







.txp_btn_ad_volume {
  background: rgba(37, 37, 37, 0.7) url(https://ca.gtimg.com/adplugin/js/imgs/mutes.png) no-repeat;
  height: 28px;
  border: none;
  border-radius: 14px;
  width: 28px;
  margin-left: 8px;
  user-select: initial;
  outline: none;
}
.txp_btn_ad_mute {
  background-position-y: -28px;
}
/* adblock */
.txp_cac_black_screen {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  width: 100%;
  z-index: 1000;
  background-color: #000;
  text-align: center;
  display: flex;
  line-height: 1.5;
  flex-direction: column;
  justify-content: center;
}
.txp_cac_black_screen .txp_cac_black_screen_title {
  margin-bottom: 15px;
  font-size: 16px;
  color: #fff;
  text-align: center;
}
.txp_cac_black_screen .txp_cac_black_screen_em {
  display: inline;
  color: #ff7000;
}

.txp_play_icon {
  width: 10%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  pointer-events: auto;
}

.txp_ad_detail {
  position: absolute;
  top: auto;
  right: 10px;
  bottom: 64px;
  left: auto;
  width: 178px;
  height: 34px;
  border-radius: 8px;
  overflow: hidden;
  pointer-events: auto;
  background-color: rgba(0, 0, 0, 0);
  transition: width 0s ease-in-out 0.5s, height 0s ease-in-out 0.5s, background-color 0.5s ease-in-out;
}
.txp_ad_detail.can-not-open a {
  cursor: default;
}
.txp_ad_detail.txp_ad_active_normal {
  width: 340px;
  height: 124px;
  background-color: rgba(0, 0, 0, 0.8);
  transition: width 0.7s ease-in-out 0.8s, height 0.7s ease-in-out 0.8s, background-color 1s ease-in-out 0.6s;
}
.txp_ad_active_normal:hover {
  background-color: #000;
}
.txp_ad_detail .active_show {
  opacity: 1;
  transition: opacity 0.3s ease-in-out;
}
.active_normal {
  display: flex;
  flex-direction: column;
  margin: 10px;
  opacity: 0;
}
.active_normal .content {
  display: flex;
  margin-bottom: 10px;
}
.txp_ad_active_normal .active_normal .txp_ad_more {
  opacity: 1;
}
.txp_ad_active_close {
  position: absolute;
  top: 3px;
  right: 3px;
  width: 16px;
  height: 16px;
  line-height: 8px;
  cursor: pointer;
}
.txp_ad_active_close img.hover {
  display: none;
}
.txp_ad_active_close:hover img.hover {
  display: block;
}
.txp_ad_active_close:hover img.not-hover {
  display: none;
}
.txp_ad_active_img {
  width: 60px;
  height: 60px;
  margin-right: 10px;
  border-radius: 4px;
  overflow: hidden;
}
.txp_ad_active_normal_text {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  width: 240px;
  height: 60px;
  font-size: 14px;
  line-height: 18px;
}
.txp_ad_active_normal_text .vendor {
  font-size: 12px;
  opacity: 0.5;
}
.txp_autohide .txp_ad_detail {
  bottom: 10px;
}
.txp_autohide .txp_ad_more {
  bottom: unset;
}
.txp_ad_detail.txp_ad_active_scan {
  width: 162px;
  height: 207px;
  background-color: rgba(0, 0, 0, 0.8);
  transition: width 0.7s ease-in-out 0.8s, height 0.7s ease-in-out 0.8s, background-color 1s ease-in-out 0.6s;
}
.active_scan {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  height: 100%;
  padding-bottom: 15px;
  color: rgba(255, 255, 255, 0.5);
  font-size: 12px;
  opacity: 0;
}
.active_scan .txp_ad_more {
  display: flex;
  justify-content: flex-start;
  height: 40px;
  padding: 0 9px;
  line-height: 40px;
  background-color: #000;
  opacity: 1;
  border-radius: 0;
}
.active_scan .item-icon {
  width: 20px;
  min-width: 20px;
  height: 20px;
  margin-right: 5px;
  border-radius: 2px;
  overflow: hidden;
}
.active_scan .item-text {
  overflow:hidden;
  text-overflow:ellipsis;
  white-space:nowrap;
}
.active_scan .txp_ad_more txpdiv {
  flex-grow: 1;
  text-align: left;
}
.active_scan .qr-code {
  width: 110px;
  height: 110px;
  padding: 4px;
  border-radius: 4px;
  background-color: #fff;
}
.active_scan .qr-code .code {
  width: 100%;
  height: 100%;
}
.txp_ad_more {
  position: relative;
  right: unset;
  bottom: unset;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 34px;
  padding: 0 16px;
  line-height: 34px;
  text-align: center;
  font-size: 14px;
  background-color: #fe5c38;
  border-radius: 16px;
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
}
.txp_ad_more img {
  width: 16px;
  height: 16px;
  margin-right: 8px;
}
.txp_ad_more:hover {
  background-color: #ff183e;
}
.txp_ad_active_scan .txp_ad_more:hover {
  background-color: #000;
}
.txp_ad_control {
  z-index: 2;
}
.txp_ad_skip {
  padding: 0 12px;
  border-radius: 12px;
}
.txp_ad_countdown {
  color: #fe5c38;
  font-size: 12px;
}
.cant-open-toast {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 40px;
  padding: 0 20px;
  line-height: 40px;
  font-size: 14px;
  color: #fff;
  background-color: rgba(0, 0, 0, .9);
  border-radius: 20px;
}
.center-qrcode {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background-color: rgba(0,0,0,.3);
  pointer-events: auto;
}
.center-qrcode .item-text {
  margin-bottom: 20px;
  color: #fff;
  font-size: 20px;
}
.center-qrcode .center-qrcode-container {
  position: relative;
  width: 260px;
  height: 300px;
  padding: 10px;
  border-radius: 2px;
  background-color: #fff;
}
.center-qrcode .center-qrcode-container .item-icon {
  position: absolute;
  top: 105px;
  left: 105px;
  width: 50px;
  height: 50px;
  border: 2px solid #fff;
  border-radius: 4px;
}
.center-qrcode .center-qrcode-container .scan-tips {
  margin-top: 15px;
  color: #3F3F4C;
  font-size: 14px;
  text-align: center;
}
.center-qrcode .close-qrcode {
  width: 26px;
  height: 26px;
  margin-top: 20px;
  cursor: pointer;
}
.center-qrcode .close-qrcode .close-hover {
  display: none;
}
.center-qrcode .close-qrcode:hover .close-not-hover {
  display: none;
}
.center-qrcode .close-qrcode:hover .close-hover {
  display: block;
}









