  white-space:nowrap;
}
.active_scan .txp_ad_more txpdiv {
  flex-grow: 1;
  text-align: left;
}
.active_scan .qr-code {
  width: 110px;
  height: 110px;
  padding: 4px;
  border-radius: 4px;
  background-color: #fff;
}
.active_scan .qr-code .code {
  width: 100%;
  height: 100%;
}
.txp_ad_more {
  position: relative;
  right: unset;
  bottom: unset;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 34px;
  padding: 0 16px;
  line-height: 34px;
