}
.txp_ad_active_scan .txp_ad_more:hover {
  background-color: #000;
}
.txp_ad_control {
  z-index: 2;
}
.txp_ad_skip {
  padding: 0 12px;
  border-radius: 12px;
}
.txp_ad_countdown {
  color: #fe5c38;
  font-size: 12px;
}
