  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 40px;
  padding: 0 20px;
  line-height: 40px;
  font-size: 14px;
  color: #fff;
  background-color: rgba(0, 0, 0, .9);
  border-radius: 20px;
}
.center-qrcode {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background-color: rgba(0,0,0,.3);
  pointer-events: auto;
}
.center-qrcode .item-text {
  margin-bottom: 20px;
  color: #fff;
  font-size: 20px;
}
.center-qrcode .center-qrcode-container {
  position: relative;
  width: 260px;
  height: 300px;
  padding: 10px;
  border-radius: 2px;
  background-color: #fff;
}
.center-qrcode .center-qrcode-container .item-icon {
  position: absolute;
  top: 105px;
  left: 105px;
  width: 50px;
  height: 50px;
  border: 2px solid #fff;
  border-radius: 4px;
}
.center-qrcode .center-qrcode-container .scan-tips {
  margin-top: 15px;
  color: #3F3F4C;
  font-size: 14px;
  text-align: center;
}
.center-qrcode .close-qrcode {
  width: 26px;
  height: 26px;
  margin-top: 20px;
  cursor: pointer;
}
.center-qrcode .close-qrcode .close-hover {
  display: none;
}
.center-qrcode .close-qrcode:hover .close-not-hover {
  display: none;
}
.center-qrcode .close-qrcode:hover .close-hover {
  display: block;
}









