  color: #fff;
  font-size: 20px;
}
.center-qrcode .center-qrcode-container {
  position: relative;
  width: 260px;
  height: 300px;
  padding: 10px;
  border-radius: 2px;
  background-color: #fff;
}
.center-qrcode .center-qrcode-container .item-icon {
  position: absolute;
  top: 105px;
  left: 105px;
  width: 50px;
  height: 50px;
  border: 2px solid #fff;
  border-radius: 4px;
}
.center-qrcode .center-qrcode-container .scan-tips {
  margin-top: 15px;
  color: #3F3F4C;
  font-size: 14px;
  text-align: center;
}
.center-qrcode .close-qrcode {
  width: 26px;
  height: 26px;
  margin-top: 20px;
  cursor: pointer;
}
.center-qrcode .close-qrcode .close-hover {
  display: none;
}
.center-qrcode .close-qrcode:hover .close-not-hover {
  display: none;
}
.center-qrcode .close-qrcode:hover .close-hover {
  display: block;
}









